import React from "react";
import "../styling/DownloadButton.css"

function DownloadButton({ Tweets }) {
    const download = () => {
        // Convert the JSON object to a string
        const jsonData = JSON.stringify(Tweets);

        // Create a Blob with the JSON data
        const blob = new Blob([jsonData], { type: "application/json" });

        // Generate a temporary URL for the Blob
        const url = window.URL.createObjectURL(blob);

        // Create a temporary link element
        const a = document.createElement("a");
        a.href = url;
        a.download = "tweets.json"; // Set the desired file name

        // Trigger a click event on the link to initiate the download
        a.click();

        // Release the temporary URL and remove the link element
        window.URL.revokeObjectURL(url);
        a.remove();
    };

    return (
        <div className={"DownloadButton"}>
            <div onClick={download}>Download current dataset</div>
        </div>
    );
}

export default DownloadButton
