import React, {useState} from "react"
import "../styling/DropDown.css"


const DropDown = ({callback, title, options}) => {

    const [selectedOption, setSelectedOption] = useState(null);
    const [showOptions, setShowOptions] = useState(false);

    const onSelect = (data) =>{
        callback(data)
    }
    const handleOptionSelect = (option) => {
        setSelectedOption(option);
        onSelect(option);
        setShowOptions(false);
    };


    return(
        <div className="dd_container">
            <div className={`dropdown-header ${showOptions ? 'active' : ''}`} onClick={() => setShowOptions(!showOptions)}>
                {selectedOption || title}
                <span className={`dropdown-arrow ${showOptions ? 'up' : 'down'}`}></span>
            </div>
            <div className={`options_menu ${showOptions ? 'active' : ''}`}>
                {options.map((option) => (
                    <li className="single_option" key={option} onClick={() => handleOptionSelect(option)}>
                        {option}
                    </li>
                ))}
            </div>
        </div>
    )
}

export default DropDown;
