import React, { useState, useRef, useEffect } from 'react';

function MultiSelectDropdown({ options, title, callback }) {
    const [selectedOptions, setSelectedOptions] = useState(new Set());
    const [showOptions, setShowOptions] = useState(false);
    const dropdownRef = useRef(null);

    const toggleOption = (option) => {
        setSelectedOptions((prevSelectedOptions) => {
            const newSelection = new Set(prevSelectedOptions);
            if (newSelection.has(option)) {
                newSelection.delete(option);
            } else {
                newSelection.add(option);
            }
            return newSelection;
        });
    };

    const handleClickOutside = (event) => {
        if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
            setShowOptions(false);
        }
    };

    useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    useEffect(() => {
        callback(selectedOptions);
    }, [selectedOptions]);

    return (
        <div className="multi-select-container">
            <div ref={dropdownRef} style={{ position: 'relative' }}>
                <div
                    className={`multi-select-header ${showOptions ? 'active' : ''}`}
                    onClick={() => setShowOptions(!showOptions)}
                >
                    {title}
                    <span
                        className={`multi-select-arrow ${showOptions ? 'down' : ''}`}
                    ></span>
                </div>
                {showOptions && (
                    <ul className="multi-select-options-menu">
                        {options.map((option) => (
                            <li
                                key={option}
                                onClick={() => toggleOption(option)}
                                className={`multi-select-option ${
                                    selectedOptions.has(option) ? 'selected' : ''
                                }`}
                            >
                                {option}
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
}

export default MultiSelectDropdown;
