import React, { useState, useRef, useEffect } from 'react';

function MultiSelectDropdown({ options, title }) {
    const [selectedOptions, setSelectedOptions] = useState(new Set());
    const [showOptions, setShowOptions] = useState(false);
    const dropdownRef = useRef(null);

    const toggleOption = (option) => {
        setSelectedOptions(prevSelectedOptions => {
            const newSelection = new Set(prevSelectedOptions);
            if (newSelection.has(option)) {
                newSelection.delete(option);
            } else {
                newSelection.add(option);
            }
            return newSelection;
        });
    };

    const handleClickOutside = (event) => {
        if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
            setShowOptions(false);
        }
    };

    useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    return (
        <div ref={dropdownRef} style={{ position: 'relative' }}>
            <button onClick={() => setShowOptions(!showOptions)}>{title}</button>
            {showOptions && (
                <ul style={{ position: 'absolute', zIndex: 1000, listStyle: 'none', padding: 0 }}>
                    {options.map(option => (
                        <li key={option} onClick={() => toggleOption(option)} style={{ cursor: 'pointer', backgroundColor: selectedOptions.has(option) ? 'lightgray' : 'white' }}>
                            {option}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default MultiSelectDropdown;
