import React, {useState} from "react";
import TimeFrameSelector from "./TimeFrameSelector";
import DropDown from "./DropDown";
import Twitter from "./Twitter";
import "../styling/Body.css"
import WordCloud from "./WordCloud";




function Body() {

    const Account_Type_Options = ["All", "Academic", "Government", "Media", "Other", "Tourism"]
    const County_Options = ["All", "Pasco", "Pinellas", "Hillsborough", "Manatee", "Sarasota"]
    const Word_Cloud_Options = ["Geo Tags", "Non-Geo Tags"]

    const [SelectedTimeFrame, setTimeFrame] = useState("")
    const [SelectedAccountType, setAccountType] = useState("")
    const [SelectedWordCloudOption, setWordCloudOption] = useState("")
    const [SelectedCountyOption, setCountyOption] = useState("")

    const [tweets, setTweets] = useState([]);

    const handleTweets = (newTweets) => {
        setTweets(newTweets);
    };

    const handleTimeFrameChange = (start, end) => {
        setTimeFrame(start + " " + end)
    };


    const callback = (value, context) => {
        switch (context) {
            case "WordCloudOption":
                setWordCloudOption(value)
                console.log("Word Cloud Option selected:", value);
                break;
            case "CountyOption":
                setCountyOption(value)
                console.log("County Option selected:", value);
                break;
            case "AccountTypeOption":
                setAccountType(value)
                console.log("Account Type Option selected:", value);
                break;
            default:
                break;
        }
    };

    return(
        <div>

            <div className="filter_menus">
                <div className="filter_item">
                    <TimeFrameSelector onTimeFrameChange={handleTimeFrameChange}/>
                </div>
                <div className="filter_item">
                    <DropDown options={Account_Type_Options}
                              title={"AccountTypeOption"}
                              callback={(value) => callback(value, "AccountTypeOption")}/>
                </div>
                <div className="filter_item">
                    <DropDown
                        callback={(value) => callback(value, "CountyOption")}
                        title="Select County"
                        options={County_Options}
                    />
                </div>

            </div>
            <div>
                <Twitter timeFrame={SelectedTimeFrame} County={SelectedCountyOption} AccountType={SelectedAccountType} onTweetsFetched={handleTweets}/>
            </div>

            <div className="word_cloud_container">
                <WordCloud tweets={tweets} cloud_type={"Non-Geo Tags"}/>
            </div>


        </div>
    )
}

export default Body
