import React, {useEffect, useState} from 'react';
import {MapContainer, TileLayer, GeoJSON} from 'react-leaflet';
import floridaCounties from './geojson-fl-counties-fips.json';
import "../styling/FloridaMap.css"


const FloridaMap = ({ userSelectedCounty }) => {
    const [, setHighlightedCounty] = useState(null);
    const [hovered, setHovered] = useState("")

    const style = (feature) => {
        if (feature.properties.NAME === userSelectedCounty) {
            return {
                fillColor: 'red',
                fillOpacity: 0.7,
                color: 'white',
            };
        }
        return {
            fillColor: 'blue',
            fillOpacity: 0.7,
            color: 'white',
        };
    };

    const onEachFeature = (county, layer) => {
        layer.on('mouseover', function (e) {
            setHovered(county.properties.NAME)
            document.getElementById('info').style.display = 'block';
        });

        layer.on('mouseout', function (e) {
            document.getElementById('info').style.display = 'none';
        });
    };

    useEffect(() => {
        if (userSelectedCounty) {
            if (userSelectedCounty !== "All") {
                setHighlightedCounty(userSelectedCounty);
            }
        } else {
            setHighlightedCounty(null);
        }
    }, [userSelectedCounty]);

    return (
        <div className="map_div">
            <MapContainer center={[27.766279, -81.686783]} zoom={6} style={{ height: 600, width: '100%' }}>
                <GeoJSON
                    data={floridaCounties}
                    style={style}
                    onEachFeature={onEachFeature}
                />
            </MapContainer>
            <p id="info" style={{display: "none"}}>{hovered}</p>
        </div>
    );
};

export default FloridaMap;
