import React, { useState } from 'react';
import '../styling/TimeFrameSelector.css'; // Assuming the CSS is in this file


function TimeFrameSelector({ onTimeFrameChange, dateRange }) {
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');

    useEffect(() => {
        setStartDate(formatDateForInput(dateRange.minDate));
        setEndDate(formatDateForInput(dateRange.maxDate));
    }, [dateRange.minDate, dateRange.maxDate]);


    const handleStartDateChange = (e) => {
        setStartDate(e.target.value);
        onTimeFrameChange(e.target.value, endDate);
    };

    const handleEndDateChange = (e) => {
        setEndDate(e.target.value);
        onTimeFrameChange(startDate, e.target.value);
    };

    return (
        <div className="time-frame-selector">
            <label>
                Start Date:
                <input type="date" value={startDate} placeholder={dateRange.minDate ? dateRange.minDate.toISOString().split('T')[0] : '08/08/2000'} onChange={handleStartDateChange} />
            </label>
            <label>
                End Date:
                <input type="date" value={endDate} placeholder={dateRange.maxDate ? dateRange.maxDate.toISOString().split('T')[0] : '08/08/2000'} onChange={handleEndDateChange} />
            </label>
        </div>
    );
}

export default TimeFrameSelector;
