import React, {useEffect, useState} from "react";



function Twitter({timeFrame, County, AccountType, onTweetsFetched}) {
    const [tweets, setTweets] = useState([]);

    const fetchTweets = () => {
        fetch('http://127.0.0.1:5000/get_tweets',
            {
                'method':'POST',
                headers : {
                    'Content-Type':'application/json'
                },
                body: JSON.stringify([timeFrame, County, AccountType])
            })
            .then((response) => response.json())
            .then((data) => {
                setTweets(data);
                onTweetsFetched(data); // Call the callback function with the fetched tweets
            })
            .catch((error) => console.error(error));
    };

    useEffect(() => {
        fetchTweets();
    }, [timeFrame, County, AccountType]);


    const [searchTerm, setSearchTerm] = useState('');

    const highlightText = (text, term) => {
        const regex = new RegExp(term, 'gi');
        return text.replace(regex, (match) => `<span class="highlight">${match}</span>`);
    };

    const filteredTweets = tweets.filter((tweet) =>
        tweet.text.toLowerCase().includes(searchTerm.toLowerCase())

    );

    const formatDate = (dateObj) => {
        let isoDateString;
        if (typeof dateObj === 'object' && dateObj.hasOwnProperty('$date')) {
            isoDateString = dateObj['$date'];
        } else {
            isoDateString = dateObj;
        }

        const date = new Date(isoDateString);
        if (isNaN(date.getTime())) {
            console.error("Invalid Date:", isoDateString);
            return "Invalid Date";
        }

        return date.toLocaleDateString("en-US", {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        });
    };
    return(
        <div>
            TWITTER
            <div className="tweets_header">
                <button className="tweet_refresher" onClick={fetchTweets}>Refresh</button>
                <input
                    className="tweet_search_bar"
                    type="text"
                    placeholder="Filter tweets"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>
        </div>
    )
}

export default Twitter;
