import React, {useState} from "react";
import TimeFrameSelector from "./TimeFrameSelector";
import DropDown from "./DropDown";
import Twitter from "./Twitter";
import "../styling/Body.css"
import WordCloud from "./WordCloud";
import FloridaMap from "./FloridaMap";
import MultiSelectDropdown from "./MultiSelectDropdown";




function Body() {

    const Account_Type_Options = ["All", "Academic", "Government", "Media", "Other", "Tourism"]
    const County_Options = ["All", "Pasco", "Pinellas", "Hillsborough", "Manatee", "Sarasota"]
    const Word_Cloud_Options = ["Geo Tags", "Non-Geo Tags"]

    const [SelectedTimeFrame, setTimeFrame] = useState("")
    const [SelectedAccountType, setAccountType] = useState("")
    const [SelectedWordCloudOption, setWordCloudOption] = useState("")
    const [SelectedCountyOption, setCountyOption] = useState("")

    const [tweets, setTweets] = useState([]);
    const [tweetCounts, setTweetCounts] = useState([]);
    const [accountOptions, setAccountOptions] = useState([])


    const handleTweets = (newTweets) => {
        setTweets(newTweets);
    };

    const handleTimeFrameChange = (start, end) => {
        setTimeFrame(start + " " + end)
    };

    const [dateRange, setDateRange] = useState({ minDate: null, maxDate: null });

    const handleDateRangeFetched = (minDate, maxDate) => {
        setDateRange({ minDate, maxDate });
    };


    const handleTweetsCounts = (counts) =>
    {
        setTweetCounts(counts)
    }


    const callback = (value, context) => {
        switch (context) {
            case "WordCloudOption":
                setWordCloudOption(value)
                console.log("Word Cloud Option selected:", value);
                break;
            case "CountyOption":
                setCountyOption(value)
                console.log("County Option selected:", value);
                break;
            case "AccountTypeOption":
                setAccountType(value)
                console.log("Account Type Option selected:", value);
                break;
            default:
                break;
        }
    };

    const handleMultiSelect = (data) => {
        console.log(data)
        setAccountOptions(data)
    }

    return(
        <div>

            <div className="filter_menus">
                <div className="filter_item">
                    <TimeFrameSelector onTimeFrameChange={handleTimeFrameChange} dateRange={dateRange}/>

                </div>
                <div className="filter_item">
                    <DropDown options={Account_Type_Options}
                              title={"AccountTypeOption"}
                              callback={(value) => callback(value, "AccountTypeOption")}/>
                </div>
                <div>
                    <MultiSelectDropdown options={Account_Type_Options} title={"AccountTypeOptions"} onChange={handleMultiSelect}/>
                </div>
                <div className="filter_item">
                    <DropDown
                        callback={(value) => callback(value, "CountyOption")}
                        title="Select County"
                        options={County_Options}
                    />
                </div>

            </div>

            <div className="App_Body">
                <div className="twitter_container">
                    <Twitter timeFrame={SelectedTimeFrame} County={SelectedCountyOption} AccountType={accountOptions} onTweetsFetched={handleTweets} onDateRangeFetched={handleDateRangeFetched}/>
                </div>

                <div className="word_cloud_container">
                    <WordCloud tweets={tweets} cloud_type={"Non-Geo Tags"} onTweetsFetched={handleTweetsCounts}/>
                </div>

                <div className="Florida_map">
                    <FloridaMap userSelectedCounty={SelectedCountyOption} tweetCounts={tweetCounts}/>
                </div>

            </div>
        </div>
    )
}

export default Body
